import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Button,
  IconButton,
  Avatar,
  Card,
  CardContent,
  Grid,
  useTheme,
  useMediaQuery,
  CircularProgress,
  Badge,
  Snackbar
} from '@mui/material';
import {
  FastfoodOutlined as FoodIcon,
  CheckCircleOutline as AcceptedIcon,
  SearchOutlined as SearchIcon,
  BarChartOutlined as StatsIcon,
  History as HistoryIcon,
  ExitToApp as LogoutIcon,
  NotificationsOutlined as NotificationIcon
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { logout } from '../../services/auth.service';
import { getAcceptedDonations, getAvailableDonations } from '../../services/donation.service';

const ReceiverDashboard = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const user = JSON.parse(localStorage.getItem('user'));

  const [stats, setStats] = useState({
    acceptedDonations: 0,
    totalMeals: 0,
    recentDonations: []
  });
  const [availableDonationCount, setAvailableDonationCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [notificationOpen, setNotificationOpen] = useState(false);

  useEffect(() => {
    fetchStats();
    fetchAvailableDonations();

    const interval = setInterval(fetchAvailableDonations, 30000);
    return () => clearInterval(interval);
  }, []);

  const fetchStats = async () => {
    try {
      const donations = await getAcceptedDonations();
      const totalMeals = donations.reduce((acc, curr) => acc + (curr.mealCount || 0), 0);
      setStats({
        acceptedDonations: donations.length,
        totalMeals,
        recentDonations: donations.slice(0, 5)
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchAvailableDonations = async () => {
    try {
      const availableDonations = await getAvailableDonations();
      if (availableDonations.length > availableDonationCount) {
        setNotificationOpen(true);
      }
      setAvailableDonationCount(availableDonations.length);
    } catch (error) {
      console.error('Error fetching available donations:', error);
    }
  };

  const handleCloseNotification = () => {
    setNotificationOpen(false);
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const quickActions = [
    {
      title: 'Available Donations',
      icon: <SearchIcon sx={{ fontSize: 40, color: '#4CAF50' }} />, 
      action: () => navigate('/receiver/available'),
      color: '#E8F5E9',
      ariaLabel: 'View available donations'
    },
    {
      title: 'Accepted Donations',
      icon: <AcceptedIcon sx={{ fontSize: 40, color: '#2196F3' }} />, 
      action: () => navigate('/receiver/accepted'),
      color: '#E3F2FD',
      ariaLabel: 'View accepted donations'
    },
    {
      title: 'Impact Stats',
      icon: <StatsIcon sx={{ fontSize: 40, color: '#FF9800' }} />, 
      action: () => navigate('/receiver/stats'),
      color: '#FFF3E0',
      ariaLabel: 'View impact statistics'
    },
    {
      title: 'History',
      icon: <HistoryIcon sx={{ fontSize: 40, color: '#9C27B0' }} />, 
      action: () => navigate('/receiver/history'),
      color: '#F3E5F5',
      ariaLabel: 'View donation history'
    }
  ];

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ bgcolor: '#F5F5F5', minHeight: '100vh' }}>
      <Container maxWidth="lg">
        <Box sx={{ py: 2, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Avatar sx={{ bgcolor: '#2196F3', width: 50, height: 50 }}>
              {user?.name?.charAt(0) || 'R'}
            </Avatar>
            <Box>
              <Typography variant="h6">Welcome back, {user?.name || 'Receiver'}!</Typography>
            </Box>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <IconButton onClick={() => navigate('/receiver/available')}>
              <Badge badgeContent={availableDonationCount} color="error">
                <NotificationIcon />
              </Badge>
            </IconButton>
            <Button variant="outlined" color="error" startIcon={<LogoutIcon />} onClick={handleLogout}>
              Logout
            </Button>
          </Box>
        </Box>
      </Container>

      <Snackbar
        open={notificationOpen}
        autoHideDuration={6000}
        onClose={handleCloseNotification}
        message="New food donations available!"
      />

      <Container maxWidth="lg">
        <Typography variant="h6" component="h2" sx={{ mb: 3 }}>
          Quick Actions
        </Typography>
        <Grid container spacing={3}>
          {quickActions.map((action, index) => (
            <Grid item xs={12} sm={6} md={3} key={index}>
              <Card onClick={action.action} sx={{ cursor: 'pointer' }}>
                <CardContent>
                  <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 2 }}>
                    <Avatar sx={{ bgcolor: action.color, width: 56, height: 56 }}>
                      {action.icon}
                    </Avatar>
                    <Typography variant="h6" align="center">
                      {action.title}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
};

export default ReceiverDashboard;
